using System;

namespace BA1A
{
    //A solution to a ROSALIND bioinformatics problem.
    //Problem Title: Compute the Number of Times a Pattern Appears in a Text
    //Rosalind ID: BA1A
    //URL: http://rosalind.info/problems/ba1a/

    class BA1A
    {

        static void Main(string[] args)
        {
            string kmer(string text, int i, int k)
            {
                //substring of text from i-th position for the next k letters
                return text.Substring(i, k);
            }
            int patterncount(string text, string pattern)
            {
                //Find a number of time pattern is within a text
                int count = 0;
                int np = pattern.Length;
                for (int i = 0; i < text.Length - np + 1; i++)
                {
                    if (kmer(text, i, np) == pattern)
                    {
                        count = count + 1;
                    }
                }
                return count;
            }

            string x = "\nAGCCTTTAG";
            string[] inlines = x.Split("\n");
            string text = inlines[0];
            string pattern = inlines[1];

            int res = patterncount(text, pattern);
            Console.WriteLine(res);
        }
    }
}
